import React, { useEffect, useState } from "react";
import Box from "@mui/material/Box";
import { DataGrid, GridColDef } from "@mui/x-data-grid";
import { MY_BU_ID, BASE_API_URL, GET_DEFAULT_HEADERS } from "../globals";

interface TestProps {
  selectedClassId: string[];
}

const columns: GridColDef[] = [
  {
    field: "studentID",
    headerName: "Student ID",
  },
  {
    field: "studentName",
    headerName: "Student Name",
    width: 150,
  },
  {
    field: "classID",
    headerName: "Class ID",
  },
  {
    field: "className",
    headerName: "Class Name",
  },
  {
    field: "semester",
    headerName: "Semester",
  },
  {
    field: "finalGrade",
    headerName: "Final Grade",
  },
];

export const Test = ({ selectedClassId }: TestProps) => {
  const [studentList, setStudentList] = useState([]);
  const [detailedStudentList, setDetailedStudentList] = useState([]);

  useEffect(() => {
    if (selectedClassId && selectedClassId[0]) {
      fetchStudents(selectedClassId[0]);
    }
  }, [selectedClassId]);

  const fetchStudents = async (classId: string) => {
    try {
      const response = await fetch(
        `${BASE_API_URL}/class/listStudents/${classId}?buid=${MY_BU_ID}`,
        {
          method: "GET",
          headers: GET_DEFAULT_HEADERS(),
        }
      );
      if (!response.ok) {
        throw new Error(`Error: ${response.status}`);
      }
      const studentsData = await response.json();
      setStudentList(studentsData);
    } catch (error) {
      console.error("Failed to fetch students enrolled in class:", error);
    }
  };


  const fetchStudentInformation = async (studentId: string) => {
    try {
      const response = await fetch(
        `${BASE_API_URL}/student/GetById/${studentId}?buid=${MY_BU_ID}`,
        {
          method: "GET",
          headers: GET_DEFAULT_HEADERS(),
        }
      );
      if (!response.ok) {
        throw new Error(`Error: ${response.status}`);
      }
      const studentInformation = await response.json();
      console.log(studentInformation[0])
      setStudentList(studentInformation);

    } catch (error) {
      console.error("Failed to fetch student information:", error);
    }
  }

  // useEffect(() => {
  //   fetchAllStudentInformation();
  // }, [studentList]);

  // const fetchAllStudentInformation = async () => {
  //   try {
  //     const studentInfoPromises = studentList.map(studentId => 
  //       fetchStudentInformation(studentId)
  //     );
  //     const details = await Promise.all(studentInfoPromises);
  //     setDetailedStudentList(details.filter(detail => detail != null));
  //   } catch (error) {
  //     console.error("Failed to fetch student information:", error);
  //   }
  // };

  let className,
    classID = selectedClassId;
  console.log(className, classID);
  return (
    <Box sx={{ height: 400, width: "100%" }}>
      <DataGrid
        rows={[
          {
            id: 1,
            studentID: "a",
            studentName: "a",
            classID: "a",
            className: "a",
            semester: "fall2022",
            finalGrade: "a",
          },
        ]}
        columns={columns}
        pageSize={5}
      />
    </Box>
  );
};
