import Box from "@mui/material/Box";
import { DataGrid, GridColDef } from "@mui/x-data-grid";
import { MY_BU_ID, BASE_API_URL, GET_DEFAULT_HEADERS } from "../globals";
import { IUniversityClass,IStudentDetail,IClassAssignment,IStudentGrade } from "../types/api_types";
import { useState, useEffect } from "react"; 

interface TestProps {
  studentDetails: IStudentDetail[];
  classTitleId: string[];
  classAssignments: IClassAssignment[];
}

const columns: GridColDef[] = [
  {
    field: "studentID",
    headerName: "Student ID",
  },
  {
    field: "studentName",
    headerName: "Student Name",
    width: 150,
  },
  {
    field: "classID",
    headerName: "Class ID",
  },
  {
    field: "className",
    headerName: "Class Name",
  },
  {
    field: "semester",
    headerName: "Semester",
  },
  {
    field: "finalGrade",
    headerName: "Final Grade",
  },
];

export const Test = ({ studentDetails, classTitleId, classAssignments }: TestProps) => {
  const [assignment, setAssignment] = useState<IClassAssignment[]>([]);
  const fetchGrades = async (studentId:string, classId:string) => {
    try {
      const res = await fetch(
        `${BASE_API_URL}/student/listGrades/${studentId}/${classId}?buid=${MY_BU_ID}`,
        {
          method: "GET",
          headers: GET_DEFAULT_HEADERS(),
        }
      );
      if (!res.ok) {
        throw new Error(`Error: ${res.status}`);
      }
      const grade = await res.json();
      setAssignment(grade)
    } catch (error) {
      console.error("Failed to fetch student grade:", error);
    }
  };
  const classTitle = classTitleId[0];
  const classId = classTitleId[1];
  const rows = studentDetails.map((student, index) => {

    if (Array.isArray(student)) {
      const s = student[0];
      const studentId = s.universityId;
      return {
        id: index,
        studentID: studentId,
        studentName: s.name,
        classID: classId,
        className: classTitle,
        semester: "fall2022",
        finalGrade:fetchGrades(studentId,classId),
      };
    } else {
      return {
        id: index,
        studentID: "loading...",
        studentName: "loading...",
        classID: classId,
        className: classTitle,
        semester: "fall2022",
        finalGrade: "Unknown",
      };
    }


  });
  return (
    <Box sx={{ height: 400, width: "100%" }}>
      <DataGrid rows={rows} columns={columns} pageSize={10} />
    </Box>
  );
};
