import React, { useEffect, useState } from "react";
import Grid from "@mui/material/Unstable_Grid2";
import { MenuItem, Select, Typography } from "@mui/material";
/**
 * You will find globals from this file useful!
 */
import { MY_BU_ID, BASE_API_URL, TOKEN, GET_DEFAULT_HEADERS } from "./globals";
import { IUniversityClass } from "./types/api_types";

import Test from "./components/Test";

function App() {
  // You will need to use more of these!
  const [currClassId, setCurrClassId] = useState<string>("");
  const [classList, setClassList] = useState<IUniversityClass[]>([]);

  const [classTitles, setClassTitles] = useState<
    { classId: string; title: string }[]
  >([]);
  const [students, setStudents] = useState([]);

  /**
   * This is JUST an example of how you might fetch some data(with a different API).
   * As you might notice, this does not show up in your console right now.
   * This is because the function isn't called by anything!
   *
   * You will need to lookup how to fetch data from an API using React.js
   * Something you might want to look at is the useEffect hook.
   *
   * The useEffect hook will be useful for populating the data in the dropdown box.
   * You will want to make sure that the effect is only called once at component mount.
   *
   * You will also need to explore the use of async/await.
   *
   */

  const fetchClasses = async () => {
    try {
      const res = await fetch(
        `${BASE_API_URL}/class/listBySemester/fall2022?buid=${MY_BU_ID}`,
        {
          method: "GET",
          headers: GET_DEFAULT_HEADERS(),
        }
      );
      if (!res.ok) {
        throw new Error(`Error: ${res.status}`);
      }
      const classes: IUniversityClass[] = await res.json();
      const transformedClasses = classes.map((item) => ({
        classId: item.classId,
        title: item.title,
      }));
      setClassTitles(transformedClasses);
    } catch (error) {
      console.error("Failed to fetch class data:", error);
    }
  };
  const fetchStudents = async (classId: string) => {
    try {
      const response = await fetch(
        `${BASE_API_URL}/class/listStudents/${classId}?buid=${MY_BU_ID}`,
        {
          method: "GET",
          headers: GET_DEFAULT_HEADERS(),
        }
      );
      if (!response.ok) {
        throw new Error(`Error: ${response.status}`);
      }
      const data = await response.json();
      setStudents(data); // Update the students state with fetched data
    } catch (error) {
      console.error("Failed to fetch student data:", error);
    }
  };

  useEffect(() => {
    fetchClasses();
  }, []);

  useEffect(() => {
    if (currClassId) {
      fetchStudents(currClassId);
    }
  }, [currClassId]);

  return (
    <div style={{ width: "100vw", height: "100vh" }}>
      <Grid container spacing={2} style={{ padding: "1rem" }}>
        <Grid xs={12} container alignItems="center" justifyContent="center">
          <Typography variant="h2" gutterBottom>
            Spark Assessment
          </Typography>
        </Grid>
        <Grid xs={12} md={4}>
          <Typography variant="h4" gutterBottom>
            Select a class
          </Typography>
          <div style={{ width: "100%" }}>
            <Select
              fullWidth={true}
              value={currClassId || ""}
              onChange={(e) => {
                const newClassId = e.target.value;
                console.log(newClassId);
                setCurrClassId(newClassId);
                fetchStudents(newClassId);
              }}
            >
              {classTitles.map((classItem) => (
                <MenuItem key={classItem.classId} value={classItem.classId}>
                  {classItem.title}
                </MenuItem>
              ))}
            </Select>
          </div>
        </Grid>
        <Grid xs={12} md={8}>
          <Typography variant="h4" gutterBottom>
            Final Grades
          </Typography>
          <div>
          <Test students={students} selectedClassId={currClassId} />
          </div>
        </Grid>
      </Grid>
    </div>
  );
}

export default App;
